for(let t of document.querySelectorAll("[data-background]")){let e=t.dataset.background;t.style.backgroundImage=`url('${e}')`}for(let t of document.querySelectorAll("[data-ratio]")){let e=t.dataset.width,a=t.dataset.height;t.style.aspectRatio=`${e} / ${a}`}for(let t of document.querySelectorAll("[data-set-width]")){let e=t.dataset.width;t.style.width=`${e}px`}
