window.addEventListener("message",(e=>{try{const t=JSON.parse(e.data),i=t.type;"resized"!=i&&"loaded"!=i||requestAnimationFrame((()=>{const i=document.getElementById("body").getBoundingClientRect();e.source.postMessage(JSON.stringify({type:"height",id:t.id,height:i.height+2*i.top}),e.origin)}))}catch(e){console.error(e)}}));
